angular.module("OWMApp",["ngRoute"]).value("owmCities",["New York","Dallas","Chicago"]).config(["$routeProvider",function(r){r.when("/",{templateUrl:"home.html",controller:"HomeCtrl"}).when("/cities/:city",{templateUrl:"city.html",controller:"CityCtrl",resolve:{city:function(r,t,e){var o=t.current.params.city;return-1===r.indexOf(o)?void e.path("/error"):o}}}).when("/error",{template:"<p>Error - Page Not Found</p>"})}]).controller("HomeCtrl",function(r){}).controller("CityCtrl",function(r,t){r.city=t});