angular.module("OWMApp",["ngRoute","ngAnimate"]).value("owmCities",["New York","Dallas","Chicago"]).config(["$routeProvider",function(t){t.when("/",{templateUrl:"home.html",controller:"HomeCtrl"}).when("/cities/:city",{templateUrl:"city.html",controller:"CityCtrl",resolve:{city:function(t,r,o){var n=r.current.params.city;return-1===t.indexOf(n)?void o.path("/error"):n}}}).when("/error",{template:"<p>Error - Page Not Found</p>"})}]).run(function(t,r,o){t.$on("$routeChangeError",function(){r.path("/error")}),t.$on("$routeChangeStart",function(){t.isLoading=!0}),t.$on("$routeChangeSuccess",function(){o(function(){t.isLoading=!1},1e3)})}).controller("HomeCtrl",function(t){}).controller("CityCtrl",function(t,r){t.city=r});